package work;

public class fourtysix {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 0;
		while(i != 4) {
			switch(i) {
			case 0:
				System.out.print(0);
				break;
			case 1:
				System.out.print(1);
			default:
				System.out.print("X");
				break;
			}
			i++;
		}
	}

}
